),
179 => 
array (
'id' => 181,
'cms_page_id' => 2,
'label' => 'label_5.5',
'value' => 'On the basis of our justified interest in personalised direct advertising, we reserve the right to store your first and last name, your postal address and - insofar as we have received this additional information from you within the scope of the contractual relationship - your title, academic degree, year of birth and your professional, industry or business name in accordance with Art. 6 Para. 1 letter f DSGVO and to use this information to send you interesting offers and information on our products by post.',
'status' => 1,
'deleted_at' => NULL,
'created_at' => '2020-10-02 13:24:22',
'updated_at' => '2020-10-02 13:24:22',
),
180 => 
array (
'id' => 182,
'cms_page_id' => 2,
'label' => 'label_5.6',
'value' => 'You can object to the storage and use of your data for this purpose at any time by sending an appropriate message to the person responsible.',
'status' => 1,
