374 => 
array (
'id' => 875,
'language_module_id' => 7,
'language_id' => 4,
'language_code' => 'es',
'column_name' => 'value',
'item_id' => 20,
'item_value' => '1.1 Las presentes Condiciones Generales de Contratación (en adelante "CGC") de TIMmunity GmbH (en adelante "vendedor") se aplican a todos los contratos de entrega de mercancías que un consumidor o empresario (en adelante "cliente") celebre con el vendedor en relación con las mercancías expuestas por el vendedor en su tienda online. Se rechaza la inclusión de las condiciones propias del cliente, a menos que se acuerde lo contrario.',
'sort_order' => 1,
'custom' => 0,
'editor' => 0,
'status' => 1,
'created_at' => '2020-10-05 15:14:53',
'updated_at' => '2021-01-07 05:51:25',
),
