274 => 
array (
'id' => 1275,
'language_module_id' => 7,
'language_id' => 5,
'language_code' => 'br',
'column_name' => 'value',
'item_id' => 20,
'item_value' => '1.1 Estes Termos e Condições Gerais (doravante denominados "TCG") da TIMmunity GmbH (doravante denominada "vendedor") aplicam-se a todos os contratos para a entrega de mercadorias que um consumidor ou empresário (doravante denominado "cliente") conclui com o vendedor em relação às mercadorias expostas pelo vendedor em sua loja on-line. A inclusão dos próprios termos e condições do cliente é rejeitada, a menos que de outra forma acordado.',
'sort_order' => 1,
'custom' => 0,
'editor' => 0,
'status' => 1,
'created_at' => '2020-10-05 15:19:41',
'updated_at' => '2021-01-07 05:51:26',
),
275 => 
array (
'id' => 1276,
'language_module_id' => 7,
