        'language_code' => 'it',
        'column_name' => 'value',
        'item_id' => 37,
        'item_value' => 'oppure - se il Cliente non dispone di un conto PayPal - soggetto alle Condizioni per i pagamenti senza conto PayPal, disponibili su',
        'sort_order' => 1,
        'custom' => 0,
        'editor' => 0,
