474 => 
array (
'id' => 2475,
'language_module_id' => 7,
'language_id' => 8,
'language_code' => 'nl',
'column_name' => 'value',
'item_id' => 20,
'item_value' => '1.1 Deze algemene voorwaarden (hierna te noemen "AV") van TIMmunity GmbH (hierna te noemen "verkoper") zijn van toepassing op alle overeenkomsten voor de levering van goederen die een consument of ondernemer (hierna te noemen "klant") met de verkoper sluit met betrekking tot de door de verkoper in zijn of haar online shop getoonde goederen. Het opnemen van eigen voorwaarden van de klant wordt hierbij afgewezen, tenzij anders is overeengekomen.',
'sort_order' => 1,
'custom' => 0,
'editor' => 0,
'status' => 1,
'created_at' => '2020-10-05 15:30:11',
'updated_at' => '2021-01-07 05:51:29',
),
